INSERT INTO `argomenti` (`aid`, `a_uid`, `nome`, `slug`, `descrizione`, `status`, `data_postato`) VALUES
(308, 95, 'Animali', 'animali', 'Il regno animale conta migliaia di creature tanto diverse l’una dall’altra. Alcune si distinguono per il loro stile di vita, altre per il loro comportamento, mentre altre stupiscono per la loro bellezza.', 0, '2022-12-04 22:31:38'),
(309, 95, 'Viaggi', 'viaggi', 'Ispirazioni con blog di viaggi su posti meno conosciuti, itinerari inconsueti, consigli per Italia, Europa, Mondo.', 0, '2022-12-04 23:11:54'),
(310, 95, 'Ricette', 'ricette', 'Le ricette della tradizione e degli chef per tutti, facili e veloci da realizzare. Tante ricette spiegate passo passo complete di foto e ingredienti. \r\nScriveteci le vostre!', 0, '2022-12-04 23:12:32'),
(311, 95, 'Droghe', 'droghe', 'Droghe pesanti e leggere. Raccontateci le vostre esperienze', 2, '2022-12-04 23:14:26'),
(312, 95, 'Ludopatia', 'ludopatia', 'Cronache relative a ludopatia e gioco d’azzardo\r\napprofondimenti su leggi e regolamenti\r\nstatistiche\r\ntestimonianze di ex-giocatori\r\nnotizie di vita associativa', 2, '2022-12-04 23:16:05'),
(313, 95, 'Alcolismo', 'alcolismo', 'Raccontateci le vostre esperienze!', 2, '2022-12-04 23:21:38');

